import React, { useState } from "react";
import PrintingInterface from "./Printlayout";

const LatestPdfViewer = () => {
  const [pdfUrl, setPdfUrl] = useState("");
  const [lastValidPdfUrl, setLastValidPdfUrl] = useState(""); // Stores last valid PDF
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [number, setNumber] = useState("");
  const [password, setPassword] = useState("");

  // Fetch the PDF
  const fetchPdf = async () => {
    if (!number || !password) {
        setError("Please enter both serial number and password.");
        setPdfUrl(""); // Clear PDF
        return;
    }

    try {
        setLoading(true);
        setError("");

        const response = await fetch(
            `http://localhost:5000/getpdf/${number}/${password}`
        );

        const data = await response.json();

        if (!response.ok) {
            throw new Error(data.error || "Invalid credentials");
        }

        setPdfUrl(data.pdfUrl);
        setError("");
    } catch (err) {
        console.error("Error fetching PDF:", err);
        setError(err.message);
        setPdfUrl(""); // Clear PDF on incorrect password
    } finally {
        setLoading(false);
    }
};


  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center justify-center p-6">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">üìÑ Latest PDF Viewer</h1>

      <div className="bg-white shadow-lg rounded-lg p-6 w-full max-w-md">
        <label className="block text-gray-700 font-semibold mb-1">
          Enter Your Serial Number
        </label>
        <input
          type="number"
          className="w-full px-3 py-2 border border-gray-300 rounded-md mb-3 focus:outline-none focus:ring-2 focus:ring-blue-500"
          placeholder="Enter number"
          value={number}
          onChange={(e) => setNumber(e.target.value)}
        />

        <label className="block text-gray-700 font-semibold mb-1">
          Enter Your Password
        </label>
        <input
          type="password"
          className="w-full px-3 py-2 border border-gray-300 rounded-md mb-3 focus:outline-none focus:ring-2 focus:ring-blue-500"
          placeholder="Enter password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />

        <button
          onClick={fetchPdf}
          className="w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-md transition duration-200"
        >
          {loading ? "Fetching..." : "Submit"}
        </button>
      </div>

      {error && (
        <div className="mt-4 bg-red-100 text-red-700 px-4 py-2 rounded">
          ‚ùå {error}
        </div>
      )}

      {pdfUrl && (
        <PrintingInterface pdfUrl={pdfUrl}/>
      )}
    </div>
  );
};

export default LatestPdfViewer;
