import React, { useState, useEffect } from "react";
import { Printer, FileText, CreditCard } from "lucide-react";
import * as pdfjsLib from 'pdfjs-dist';

// Set the worker source
pdfjsLib.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjsLib.version}/pdf.worker.min.js`;

const PrintingInterface = ({pdfUrl}) => {
  // Rest of the code remains exactly the same, just changing how we reference pdfjs
  const [copies, setCopies] = useState(1);
  const [orientation, setOrientation] = useState("portrait");
  const [selectedPages, setSelectedPages] = useState("all");
  const [customPageRange, setCustomPageRange] = useState("");
  const [totalPages, setTotalPages] = useState(0);
  const [colorMode, setColorMode] = useState("bw");
  const [sidedMode, setSidedMode] = useState("single");

  // Price per page
  const pricePerPage = colorMode === "bw" ? 2 : 10;

  // Function to load PDF and get total pages
  const loadPdfDetails = async () => {
    try {
      const loadingTask = pdfjsLib.getDocument(pdfUrl);
      const pdf = await loadingTask.promise;
      setTotalPages(pdf.numPages);
      console.log(totalPages);
      
    } catch (error) {
      console.error("Error loading PDF:", error);
      setTotalPages(0);
    }
  };

  useEffect(() => {
    loadPdfDetails();
}, [pdfUrl, loadPdfDetails]); 

const handlePrint = async () => {
    const printData = {
      pdfUrl,
      copies,
      orientation,
      colorMode,
      sidedMode,
      selectedPages,
      customPageRange: selectedPages === "custom" ? customPageRange : null,
    };
  
    try {
      const response = await fetch("http://localhost:5000/print", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(printData),
      });
  
      const result = await response.json();
      if (result.success) {
        alert("Printing started successfully!");
      } else {
        alert("Printing failed: " + result.error);
      }
    } catch (error) {
      console.error("Error sending print request:", error);
      alert("Printing failed. Please try again.");
    }
  };
  


  // Calculate the number of pages to print
  const calculatePageCount = () => {
    if (selectedPages === "custom") {
      try {
        const pages = new Set();
        const ranges = customPageRange.split(",");
        
        ranges.forEach((range) => {
          range = range.trim();
          if (range.includes("-")) {
            const [start, end] = range.split("-").map(Number);
            if (!isNaN(start) && !isNaN(end) && start <= end) {
              for (let i = start; i <= Math.min(end, totalPages); i++) {
                pages.add(i);
              }
            }
          } else {
            const page = Number(range);
            if (!isNaN(page) && page <= totalPages) {
              pages.add(page);
            }
          }
        });
        
        return pages.size;
      } catch (error) {
        return 0;
      }
    } else if (selectedPages === "odd") {
      return Math.ceil(totalPages / 2);
    } else if (selectedPages === "even") {
      return Math.floor(totalPages / 2);
    }
    return totalPages;
  };

  // Calculate the total price
  const calculateTotal = () => {
    const pageCount = calculatePageCount();
    let totalSheets = pageCount; // Default for single-sided
    let totalPrice = 0;

    if (colorMode === "bw") {
        if (sidedMode === "double") {
            totalSheets = Math.ceil(pageCount / 2); // Two pages per sheet (round up)
            totalPrice = totalSheets * copies * 1.75; // ₹1.75 per side for B&W
        } else {
            totalPrice = pageCount * copies * 2; // ₹2 per page for single-sided B&W
        }
    } else {
        // Color printing (₹10 per printed page, whether single or double-sided)
        totalSheets = sidedMode === "double" ? Math.ceil(pageCount / 2) : pageCount;
        totalPrice = totalSheets * copies * 10; // ₹10 per printed page
    }

    return totalPrice;
};



  return (
    <div className=" p-2">
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {/* Document Preview Section */}
        <div className="bg-white shadow-md rounded-lg p-4">
          <div className="flex items-center space-x-2 border-b pb-2 mb-4">
            <FileText className="h-5 w-5 text-gray-600" />
            <h3 className="font-semibold text-lg">Document Preview</h3>
          </div>
          <div className="bg-gray-100 rounded-lg">
            <iframe
              src={pdfUrl}
              title="PDF Preview"
              className="w-full h-[600px] rounded-lg"
            />
          </div>
        </div>

        {/* Print Settings */}
        <div className="bg-white shadow-md rounded-lg p-4">
          <div className="flex items-center space-x-2 border-b pb-2 mb-4">
            <Printer className="h-5 w-5 text-gray-600" />
            <h3 className="font-semibold text-lg">Print Settings</h3>
          </div>

          {/* Copies */}
          <div className="mb-4">
            <label className="block text-sm font-medium mb-2">Copies</label>
            <input
              type="number"
              min="1"
              value={copies}
              onChange={(e) => setCopies(Math.max(1, parseInt(e.target.value) || 1))}
              className="w-20 p-2 border rounded-md focus:ring focus:ring-blue-300"
            />
          </div>

          {/* Orientation */}
          <div className="mb-4">
            <label className="block text-sm font-medium mb-2">Layout</label>
            <div className="space-x-4">
              <label className="inline-flex items-center">
                <input
                  type="radio"
                  checked={orientation === "portrait"}
                  onChange={() => setOrientation("portrait")}
                  className="mr-2"
                />
                Portrait
              </label>
              <label className="inline-flex items-center">
                <input
                  type="radio"
                  checked={orientation === "landscape"}
                  onChange={() => setOrientation("landscape")}
                  className="mr-2"
                />
                Landscape
              </label>
            </div>
          </div>

          {/* Color Mode */}
          <div className="mb-4">
            <label className="block text-sm font-medium mb-2">Color</label>
            <div className="space-x-4">
              <label className="inline-flex items-center">
                <input
                  type="radio"
                  checked={colorMode === "bw"}
                  onChange={() => setColorMode("bw")}
                  className="mr-2"
                />
                Black & White (₹3/page)
              </label>
              <label className="inline-flex items-center">
                <input
                  type="radio"
                  checked={colorMode === "color"}
                  onChange={() => setColorMode("color")}
                  className="mr-2"
                />
                Color (₹10/page)
              </label>
            </div>
          </div>

          {/* Sided Mode */}
          <div className="mb-4">
            <label className="block text-sm font-medium mb-2">Sided</label>
            <div className="space-x-4">
              <label className="inline-flex items-center">
                <input
                  type="radio"
                  checked={sidedMode === "single"}
                  onChange={() => setSidedMode("single")}
                  className="mr-2"
                />
                Single-Sided
              </label>
              <label className="inline-flex items-center">
                <input
                  type="radio"
                  checked={sidedMode === "double"}
                  onChange={() => setSidedMode("double")}
                  className="mr-2"
                />
                Double-Sided
              </label>
            </div>
          </div>

          {/* Page Selection */}
          <div className="mb-4">
            <label className="block text-sm font-medium mb-2">Pages</label>
            <div className="space-y-2">
              <label className="block">
                <input
                  type="radio"
                  checked={selectedPages === "all"}
                  onChange={() => setSelectedPages("all")}
                  className="mr-2"
                />
                All pages ({totalPages} pages)
              </label>
              <label className="block">
                <input
                  type="radio"
                  checked={selectedPages === "odd"}
                  onChange={() => setSelectedPages("odd")}
                  className="mr-2"
                />
                Odd pages only
              </label>
              <label className="block">
                <input
                  type="radio"
                  checked={selectedPages === "even"}
                  onChange={() => setSelectedPages("even")}
                  className="mr-2"
                />
                Even pages only
              </label>
              <label className="block">
                <input
                  type="radio"
                  checked={selectedPages === "custom"}
                  onChange={() => setSelectedPages("custom")}
                  className="mr-2"
                />
                Custom pages:
                <input
                  type="text"
                  value={customPageRange}
                  onChange={(e) => setCustomPageRange(e.target.value)}
                  placeholder="e.g., 1-3 or 1,2,3"
                  className="ml-2 p-1 border rounded-md"
                  disabled={selectedPages !== "custom"}
                />
              </label>
            </div>
          </div>

          {/* Bill Summary */}
          <div className="bg-gray-50 p-4 rounded-lg shadow-inner mt-4">
            <h4 className="font-semibold mb-2">Bill Summary</h4>
            <div className="space-y-2 text-sm">
              <div className="flex justify-between">
                <span>Price per page:</span>
                <span>₹{pricePerPage}</span>
              </div>
              <div className="flex justify-between">
                <span>Number of pages:</span>
                <span>{calculatePageCount()}</span>
              </div>
              <div className="flex justify-between">
                <span>Number of copies:</span>
                <span>{copies}</span>
              </div>
              <div className="flex justify-between font-semibold border-t pt-2">
                <span>Total Amount:</span>
                <span>₹{calculateTotal()}</span>
              </div>
            </div>
          </div>

          {/* Pay and Print Button */}
          <button   onClick={handlePrint}  className="w-full mt-4 flex items-center justify-center bg-blue-600 hover:bg-blue-700 text-white py-2 px-4 rounded-md transition-colors">
            <CreditCard className="w-4 h-4 mr-2" />
            Pay and Print
          </button>
        </div>
      </div>
    </div>
  );
};

export default PrintingInterface;
