import React, { useState, useEffect } from "react";
import { Printer, FileText, CreditCard } from "lucide-react";
import * as pdfjs from 'pdfjs-dist';

const PrintingInterface = () => {
  const [copies, setCopies] = useState(1);
  const [orientation, setOrientation] = useState("portrait");
  const [selectedPages, setSelectedPages] = useState("all");
  const [customPageRange, setCustomPageRange] = useState("");
  const [totalPages, setTotalPages] = useState(0);
  const [colorMode, setColorMode] = useState("bw");
  const [sidedMode, setSidedMode] = useState("single");
  const [isLoading, setIsLoading] = useState(true);

  // Initialize PDF.js worker
  useEffect(() => {
    pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.min.js`;
  }, []);

  // Function to get PDF page count
  useEffect(() => {
    const getPDFPageCount = async () => {
      try {
        setIsLoading(true);
        const pdfUrl = "https://venor-printing.s3.eu-north-1.amazonaws.com/1_5678.pdf";
        const loadingTask = pdfjs.getDocument(pdfUrl);
        const pdf = await loadingTask.promise;
        setTotalPages(pdf.numPages);
      } catch (error) {
        console.error("Error loading PDF:", error);
        setTotalPages(0);
      } finally {
        setIsLoading(false);
      }
    };

    getPDFPageCount();
  }, []);

  // Price per page
  const pricePerPage = colorMode === "bw" ? 3 : 10;

  // Calculate the number of pages to print
  const calculatePageCount = () => {
    if (selectedPages === "custom") {
      const pages = [];
      const ranges = customPageRange.split(",");
      ranges.forEach((range) => {
        if (range.includes("-")) {
          const [start, end] = range.split("-").map(Number);
          for (let i = start; i <= end && i <= totalPages; i++) {
            if (!pages.includes(i)) pages.push(i);
          }
        } else {
          const pageNum = Number(range);
          if (pageNum <= totalPages && !pages.includes(pageNum)) {
            pages.push(pageNum);
          }
        }
      });
      return pages.length;
    } else if (selectedPages === "odd") {
      return Math.ceil(totalPages / 2);
    } else if (selectedPages === "even") {
      return Math.floor(totalPages / 2);
    } else {
      return totalPages;
    }
  };

  // Calculate the total price
  const calculateTotal = () => {
    const pageCount = calculatePageCount();
    const totalPrice = pageCount * copies * pricePerPage;
    return sidedMode === "double" ? totalPrice / 2 : totalPrice;
  };

  return (
    <div className="mx-auto p-6">
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {/* Document Preview Section */}
        <div className="bg-white shadow-md rounded-lg p-4">
          <div className="flex items-center space-x-2 border-b pb-2 mb-4">
            <FileText className="h-5 text-gray-600" />
            <h3 className="font-semibold text-lg">
              Document Preview {isLoading ? "(Loading...)" : `(${totalPages} pages)`}
            </h3>
          </div>
          <div className="bg-gray-100 rounded-lg flex items-center justify-center">
            <iframe
              src="https://venor-printing.s3.eu-north-1.amazonaws.com/1_5678.pdf"
              title="PDF Viewer"
              width="100%"
              height="600px"
              className="border rounded-md"
            />
          </div>
        </div>

        {/* Print Settings */}
        <div className="bg-white shadow-md rounded-lg p-4">
          <div className="flex items-center space-x-2 border-b pb-2 mb-4">
            <Printer className="w-5 h-5 text-gray-600" />
            <h3 className="font-semibold text-lg">Print Settings</h3>
          </div>

          {isLoading ? (
            <div className="text-center py-4">Loading PDF information...</div>
          ) : (
            <>
              {/* Copies */}
              <div className="mb-4">
                <label className="block text-sm font-medium mb-2">Copies</label>
                <input
                  type="number"
                  min="1"
                  value={copies}
                  onChange={(e) => setCopies(parseInt(e.target.value) || 1)}
                  className="w-20 p-2 border rounded-md focus:ring focus:ring-blue-300"
                />
              </div>

              {/* Orientation */}
              <div className="mb-4">
                <label className="block text-sm font-medium mb-2">Layout</label>
                <div className="space-x-4">
                  <label className="inline-flex items-center">
                    <input
                      type="radio"
                      checked={orientation === "portrait"}
                      onChange={() => setOrientation("portrait")}
                      className="mr-2"
                    />
                    Portrait
                  </label>
                  <label className="inline-flex items-center">
                    <input
                      type="radio"
                      checked={orientation === "landscape"}
                      onChange={() => setOrientation("landscape")}
                      className="mr-2"
                    />
                    Landscape
                  </label>
                </div>
              </div>

              {/* Color Mode */}
              <div className="mb-4">
                <label className="block text-sm font-medium mb-2">Color</label>
                <div className="space-x-4">
                  <label className="inline-flex items-center">
                    <input
                      type="radio"
                      checked={colorMode === "bw"}
                      onChange={() => setColorMode("bw")}
                      className="mr-2"
                    />
                    Black & White (₹3/page)
                  </label>
                  <label className="inline-flex items-center">
                    <input
                      type="radio"
                      checked={colorMode === "color"}
                      onChange={() => setColorMode("color")}
                      className="mr-2"
                    />
                    Color (₹10/page)
                  </label>
                </div>
              </div>

              {/* Sided Mode */}
              <div className="mb-4">
                <label className="block text-sm font-medium mb-2">Sided</label>
                <div className="space-x-4">
                  <label className="inline-flex items-center">
                    <input
                      type="radio"
                      checked={sidedMode === "single"}
                      onChange={() => setSidedMode("single")}
                      className="mr-2"
                    />
                    Single-Sided
                  </label>
                  <label className="inline-flex items-center">
                    <input
                      type="radio"
                      checked={sidedMode === "double"}
                      onChange={() => setSidedMode("double")}
                      className="mr-2"
                    />
                    Double-Sided
                  </label>
                </div>
              </div>

              {/* Page Selection */}
              <div className="mb-4">
                <label className="block text-sm font-medium mb-2">Pages</label>
                <div className="space-y-2">
                  <label className="block">
                    <input
                      type="radio"
                      checked={selectedPages === "all"}
                      onChange={() => setSelectedPages("all")}
                      className="mr-2"
                    />
                    All pages
                  </label>
                  <label className="block">
                    <input
                      type="radio"
                      checked={selectedPages === "odd"}
                      onChange={() => setSelectedPages("odd")}
                      className="mr-2"
                    />
                    Odd pages only
                  </label>
                  <label className="block">
                    <input
                      type="radio"
                      checked={selectedPages === "even"}
                      onChange={() => setSelectedPages("even")}
                      className="mr-2"
                    />
                    Even pages only
                  </label>
                  <label className="block">
                    <input
                      type="radio"
                      checked={selectedPages === "custom"}
                      onChange={() => setSelectedPages("custom")}
                      className="mr-2"
                    />
                    Custom pages:
                    <input
                      type="text"
                      value={customPageRange}
                      onChange={(e) => setCustomPageRange(e.target.value)}
                      placeholder={`e.g., 1-${totalPages} or 1,2`}
                      className="ml-2 p-1 border rounded-md"
                    />
                  </label>
                </div>
              </div>

              {/* Bill Summary */}
              <div className="bg-gray-50 p-4 rounded-lg shadow-inner mt-4">
                <h4 className="font-semibold mb-2">Bill Summary</h4>
                <div className="space-y-2 text-sm">
                  <div className="flex justify-between">
                    <span>Price per page:</span>
                    <span>₹{pricePerPage}</span>
                  </div>
                  <div className="flex justify-between">
                    <span>Number of pages:</span>
                    <span>{calculatePageCount()}</span>
                  </div>
                  <div className="flex justify-between">
                    <span>Number of copies:</span>
                    <span>{copies}</span>
                  </div>
                  <div className="flex justify-between font-semibold border-t pt-2">
                    <span>Total Amount:</span>
                    <span>₹{calculateTotal()}</span>
                  </div>
                </div>
              </div>

              {/* Pay and Print Button */}
              <button className="w-full mt-4 flex items-center justify-center bg-blue-600 hover:bg-blue-700 text-white py-2 rounded-md transition">
                <CreditCard className="w-4 h-4 mr-2" />
                Pay and Print
              </button>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default PrintingInterface;
