import React, { useState, useEffect } from "react";

const LatestPdfViewer = () => {
  const [pdfUrl, setPdfUrl] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(true);

  // Fetch the latest PDF
  const fetchLatestPdf = async () => {
    try {
      setLoading(true);
      const response = await fetch("http://localhost:5000/latest-pdf");

      if (!response.ok) {
        throw new Error("Failed to fetch the latest PDF");
      }

      const data = await response.json();
      setPdfUrl(data.pdfUrl);
      setError("");
    } catch (error) {
      console.error("Error fetching latest PDF:", error);
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchLatestPdf();
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center justify-center p-6">
      <h1 className="text-2xl font-bold text-gray-800 mb-4">Latest PDF Viewer</h1>

      {loading && <p className="text-gray-600">Loading latest PDF...</p>}

      {error && (
        <div className="bg-red-100 text-red-700 px-4 py-2 rounded mb-4">
          {error}
        </div>
      )}

      {pdfUrl && (
        <div className="w-full max-w-3xl bg-white shadow-md rounded-lg p-4">
          

          <div className="mt-4 text-center">
           {pdfUrl}
           <div style={{ width: '100%', height: '100vh', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
      {pdfUrl ? (
        <iframe
          src={pdfUrl}
          title="PDF Viewer"
          width="80%"
          height="600px"
          style={{ border: 'none' }}
        />
      ) : (
        <div>No PDF URL provided</div>
      )}
    </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default LatestPdfViewer;
