import React, { useState, useRef } from 'react';
import { Upload, Pen, Check, X } from 'lucide-react';

const FileUpload = () => {
  const [file, setFile] = useState(null);
  const [isDrawing, setIsDrawing] = useState(false);
  const [signature, setSignature] = useState(null);
  const [error, setError] = useState('');
  const canvasRef = useRef(null);
  const [lastPos, setLastPos] = useState({ x: 0, y: 0 });

  const handleFileChange = (event) => {
    const selectedFile = event.target.files[0];
    setError('');
    
    if (selectedFile) {
      // Check file size (e.g., max 5MB)
      if (selectedFile.size > 5 * 1024 * 1024) {
        setError('File size should be less than 5MB');
        return;
      }
      
      // Check file type
      const allowedTypes = ['application/pdf', 'application/msword', 'application/vnd.openxmlformats-officedocument.wordprocessingml.document'];
      if (!allowedTypes.includes(selectedFile.type)) {
        setError('Please upload PDF or Word documents only');
        return;
      }
      
      setFile(selectedFile);
    }
  };

  const startDrawing = (e) => {
    setIsDrawing(true);
    const pos = getTouchPos(canvasRef.current, e);
    setLastPos(pos);
  };

  const draw = (e) => {
    if (!isDrawing) return;
    e.preventDefault();
    
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const pos = getTouchPos(canvas, e);
    
    ctx.beginPath();
    ctx.moveTo(lastPos.x, lastPos.y);
    ctx.lineTo(pos.x, pos.y);
    ctx.strokeStyle = '#000';
    ctx.lineWidth = 2;
    ctx.lineCap = 'round';
    ctx.stroke();
    
    setLastPos(pos);
  };

  const getTouchPos = (canvas, e) => {
    const rect = canvas.getBoundingClientRect();
    const touch = e.touches[0];
    return {
      x: touch.clientX - rect.left,
      y: touch.clientY - rect.top
    };
  };

  const stopDrawing = () => {
    setIsDrawing(false);
    const canvas = canvasRef.current;
    setSignature(canvas.toDataURL());
  };

  const clearSignature = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    setSignature(null);
  };

  const saveDocument = async () => {
    try {
      if (!file || !signature) {
        setError('Please upload a document and add your signature');
        return;
      }

      // Here you would implement your storage logic
      console.log('Saving document with signature...');
      
      // Example of what you might do:
      // const formData = new FormData();
      // formData.append('file', file);
      // formData.append('signature', signature);
      // await axios.post('/api/upload', formData);
      
    } catch (err) {
      setError('Failed to save document. Please try again.');
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-4">
      {/* Header */}
      <div className="text-center mb-6">
        <h1 className="text-2xl font-bold text-gray-800">Document Signing</h1>
        <p className="text-sm text-gray-600">Upload and sign your document</p>
      </div>

      {/* Error Message */}
      {error && (
        <div className="bg-red-50 border border-red-200 text-red-600 px-4 py-3 rounded-lg mb-4">
          {error}
        </div>
      )}

      {/* File Upload Section */}
      <div className="bg-white rounded-lg shadow-md p-4 mb-4">
        <div className="border-2 border-dashed border-gray-300 rounded-lg p-4">
          <input
            type="file"
            onChange={handleFileChange}
            className="hidden"
            id="file-upload"
            accept=".pdf,.doc,.docx"
          />
          <label
            htmlFor="file-upload"
            className="flex flex-col items-center cursor-pointer"
          >
            <Upload className="w-8 h-8 text-blue-500 mb-2" />
            <span className="text-sm text-gray-600">
              {file ? file.name : 'Tap to upload document'}
            </span>
          </label>
        </div>
      </div>

      {/* Signature Section */}
      <div className="bg-white rounded-lg shadow-md p-4 mb-4">
        <div className="flex items-center justify-between mb-2">
          <h2 className="text-lg font-semibold flex items-center">
            <Pen className="w-5 h-5 mr-2" />
            Your Signature
          </h2>
          <button
            onClick={clearSignature}
            className="text-red-500 p-2"
            aria-label="Clear signature"
          >
            <X className="w-5 h-5" />
          </button>
        </div>
        
        <div className="border rounded-lg">
          <canvas
            ref={canvasRef}
            width={300}
            height={200}
            className="touch-none w-full bg-gray-50"
            onTouchStart={startDrawing}
            onTouchMove={draw}
            onTouchEnd={stopDrawing}
          />
        </div>
      </div>

      {/* Action Buttons */}
      <div className="fixed bottom-0 left-0 right-0 p-4 bg-white border-t">
        <button
          onClick={saveDocument}
          disabled={!file || !signature}
          className={`w-full py-3 rounded-lg flex items-center justify-center space-x-2
            ${file && signature 
              ? 'bg-blue-500 text-white hover:bg-blue-600 transition-colors' 
              : 'bg-gray-300 text-gray-500'}`}
        >
          <Check className="w-5 h-5" />
          <span>Save and Sign</span>
        </button>
      </div>
    </div>
  );
};

export default FileUpload;
