import React, { useState, useRef } from 'react';
import { Upload } from 'lucide-react';

export default function S3Upload() {
  const [file, setFile] = useState(null);
  const [isUploading, setIsUploading] = useState(false);
  const [message, setMessage] = useState({ text: '', type: '' });
  const fileRef = useRef(null);

  const handleUpload = async () => {
    if (!file) return;
    
    setIsUploading(true);
    const formData = new FormData();
    formData.append('file', file);

    try {
      const res = await fetch('http://localhost:5000/upload', {
        method: 'POST',
        body: formData,
        headers: {
          'Accept': 'application/json',
        },
        mode: 'cors',
      });
      
      if (!res.ok) throw new Error('Upload failed');
      
      const data = await res.json();
      setMessage({ text: 'Upload successful', type: 'success' });
      setFile(null);
      if (fileRef.current) fileRef.current.value = '';
      
    } catch (err) {
      setMessage({ text: err.message || 'Upload failed', type: 'error' });
    } finally {
      setIsUploading(false);
    }
  };

  const onFileChange = (e) => {
    const selected = e.target.files[0];
    if (selected) {
      setMessage({ text: '', type: '' });
      setFile(selected);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 flex items-center justify-center">
      <div className="w-full max-w-md bg-white rounded-lg shadow p-6">
        <h1 className="text-xl font-semibold mb-4">Upload Document to S3</h1>
        
        {message.text && (
          <div className={`p-3 rounded mb-4 ${
            message.type === 'error' ? 'bg-red-100 text-red-700' : 'bg-green-100 text-green-700'
          }`}>
            {message.text}
          </div>
        )}

        <div 
          className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center"
          onDragOver={(e) => e.preventDefault()}
          onDrop={(e) => {
            e.preventDefault();
            const droppedFile = e.dataTransfer.files[0];
            if (droppedFile) {
              setFile(droppedFile);
              setMessage({ text: '', type: '' });
            }
          }}
        >
          <input
            type="file"
            ref={fileRef}
            onChange={onFileChange}
            className="hidden"
            accept=".pdf,.doc,.docx"
          />
          
          <div className="flex flex-col items-center gap-2">
            <Upload className="w-12 h-12 text-gray-400" />
            <span className="text-gray-500">
              {file ? `Selected: ${file.name}` : 'Choose file or drag & drop'}
            </span>
            <button 
              onClick={() => fileRef.current?.click()}
              className="text-blue-500 hover:text-blue-600"
            >
              Browse Files
            </button>
          </div>
        </div>

        <button
          onClick={handleUpload}
          disabled={!file || isUploading}
          className={`w-full mt-4 p-2 rounded-lg transition-colors ${
            file && !isUploading
              ? 'bg-blue-500 hover:bg-blue-600 text-white'
              : 'bg-gray-200 text-gray-500 cursor-not-allowed'
          }`}
        >
          {isUploading ? 'Uploading...' : 'Upload'}
        </button>
      </div>
    </div>
  );
}
